import React, { useState } from 'react';
import './LoginForm.css'; // Archivo de estilos (crear después)

const LoginForm = () => {
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    
    // Simulación de carga (sin conexión real)
    setTimeout(() => {
      console.log('Datos simulados:', formData);
      setLoading(false);
      alert('Login simulado (sin backend)');
    }, 1500);
  };

  return (
    <div className="wiki-login-container">
      <div className="wiki-login-prompt">
        <p>Necesitas tener <i>cookies</i> permitidos para poder registrarte.</p>
      </div>
      
      <form onSubmit={handleSubmit} className="wiki-login-form">
        <div className="wiki-form-group">
          <label htmlFor="email">Correo electrónico</label>
          <input
            type="email"
            id="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            placeholder="usuario@example.com"
            required
          />
        </div>
        
        <div className="wiki-form-group">
          <label htmlFor="password">Contraseña</label>
          <input
            type="password"
            id="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            placeholder="••••••••"
            required
          />
        </div>
        
        <div className="wiki-remember-me">
          <input
            type="checkbox"
            id="remember"
            name="remember"
          />
          <label htmlFor="remember">Mantener mi sesión iniciada</label>
        </div>
        
        {error && <div className="wiki-error-message">{error}</div>}
        
        <button 
          type="submit" 
          className="wiki-login-button" 
          disabled={loading}
        >
          {loading ? 'Cargando...' : 'Acceder'}
        </button>
        
        <div className="wiki-form-links">
          <a href="/olvide-contrasena" className="wiki-forgot-password">
            ¿Olvidaste tu contraseña?
          </a>
          <div className="wiki-create-account">
            ¿No tienes cuenta? <a href="/registro">Regístrate aquí</a>
          </div>
        </div>
      </form>
    </div>
  );
};

export default LoginForm;
