import React, { useState } from 'react';
import './LoginForm.css';

const WikipediaLogin = () => {
  const [formData, setFormData] = useState({
    username: '',
    password: ''
  });
  const [remember, setRemember] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Aquí iría la conexión con el backend
    alert('¡Bienvenido a Wikipedia!');
  };

  return (
    <div className="wikipedia-bg">
      <div className="wikipedia-login-wrapper">
        <div className="wikipedia-logo">
          <img
            src="https://upload.wikimedia.org/wikipedia/commons/6/63/Wikipedia-logo.png"
            alt="Wikipedia"
          />
        </div>
        <h1 className="wikipedia-title">Wikipedia</h1>
        <p className="wikipedia-subtitle">La enciclopedia libre</p>
        <form onSubmit={handleSubmit} className="wikipedia-login-form">
          <div className="form-group">
            <label htmlFor="username">Nombre de usuario</label>
            <input
              type="text"
              id="username"
              name="username"
              autoComplete="username"
              value={formData.username}
              onChange={handleChange}
              required
              autoFocus
            />
          </div>
          <div className="form-group">
            <label htmlFor="password">Contraseña</label>
            <input
              type="password"
              id="password"
              name="password"
              autoComplete="current-password"
              value={formData.password}
              onChange={handleChange}
              required
            />
          </div>
          <div className="form-options">
            <div className="remember-me">
              <input
                type="checkbox"
                id="remember"
                checked={remember}
                onChange={() => setRemember(!remember)}
              />
              <label htmlFor="remember">Mantener mi sesión iniciada</label>
            </div>
            <div className="forgot-link">
              <a href="/olvide-contrasena">¿Has olvidado tu contraseña?</a>
            </div>
          </div>
          <button type="submit" className="login-button">Acceder</button>
        </form>
        <div className="login-links">
          <a href="/crear-cuenta">Crear una cuenta</a> ·
          <a href="/ayuda-acceso">Ayuda</a>
        </div>
        <div className="login-languages">
          <span>Otros idiomas: </span>
          <a href="#">English</a> ·
          <a href="#">Deutsch</a> ·
          <a href="#">Français</a> ·
          <a href="#">Italiano</a>
        </div>
      </div>
    </div>
  );
};

export default WikipediaLogin;
